import { useParams } from 'react-router-dom';

const Icon = ({ name }) => {
  const path = `/assets/icons/${name}.svg`;

  return (
    <svg viewBox="0 0 24 24" fill="currentColor" width="24" height="24">
      <use xlinkHref={path} />
    </svg>
  );
};

const About = () => {
  const { id } = useParams();

  return (
    <main className="section">
      <div className="container">
        <h1 className="about">About</h1>
        <br />
        <div className="about-me">
          <p>
            My name is Pavlo. I am a full stack developer.
          </p>
          <br />
          <ul>
            <li>
              My skills:
              <ul>
                <li>
                 
                </li>
                <li>CSS</li>
                <li>JavaScript</li>
                <li>React</li>
                <li>Python</li>
                <li>SQL</li>
              </ul>
              <br />
            </li>
            <li>
              My projects:
              <ul>
                <li>Сайт для [назва сайту]</li>
                <li>Аплікація для [назва аплікації]</li>
              </ul>
            </li>
          </ul>
        </div>
      </div>
    </main>
  );
};

export default About;
