const About = () => {
  const { id } = useParams();

  return (
    <main className="section">
      <div className="container">
      <h1 class="about">About</h1><br></br>
      <div class="about-me">
  <p>
    My name is Pavlo. I am a full stack developer
  </p><br></br>
  <ul>
    {skills.map((skill, index) => (
      <li key={index}>
        <img src={skillIcon(skill)} alt={skill} />
        {skill}
      </li>
    ))}
  </ul>
</div>

      
      </div>
    </main>
  );
};

const skillIcon = (skill) => {
  switch (skill) {
    case "HTML":
      return "/assets/images/icons/iconhtml.png";
    case "CSS":
      return "/assets/images/icons/iconcss.png";
    case "JavaScript":
      return "/assets/images/icons/iconjs.png";
    case "React":
      return "/assets/images/icons/iconreact.png";
    case "Python":
      return "/assets/images/icons/iconpython.png";
    case "SQL":
      return "/assets/images/icons/iconsql.png";
    default:
      return "";
  }
};

export default About;
